export function offlineReward(ratePerSec: number, offlineSec: number) {
  const capSec = 8 * 60 * 60; // 8 小时上限
  const t = Math.min(offlineSec, capSec);
  const base = ratePerSec * t;
  const bonus = 1 + Math.min(0.5, Math.log10(1 + t / 60) * 0.2); // 温和加成
  return Math.floor(base * bonus);
}
