export function rareProb(pity: number) {
  // pity: 连续未出稀有的次数
  const base = 0.02; // 2%
  const hard = 80; // 80 抽硬保底
  if (pity >= hard - 1) return 1;

  // 60 抽后开始“软保底”线性爬升（示例）
  const softStart = 60;
  if (pity < softStart) return base;

  const t = (pity - softStart) / (hard - softStart);
  return base + t * (0.25 - base); // 最终爬到 25%
}
