export function costFor(level: number) {
  const base = 50;
  const r = level < 20 ? 1.08 : level < 80 ? 1.11 : 1.14;
  return Math.floor(base * Math.pow(r, level));
}

export function milestoneFor(level: number) {
  // 把“无限”切成阶段，解锁新系统
  if (level === 10) return "解锁自动点击";
  if (level === 30) return "解锁转生";
  if (level === 60) return "解锁新资源";
  return null;
}
