type Phase = "setup" | "mix" | "resolve" | "success" | "fail";

export function nextPhase(phase: Phase, action: string): Phase {
  if (phase === "setup" && action === "pull" ) return "mix";
  if (phase === "mix" && action === "pull" ) return "resolve";
  if (phase === "resolve" && action === "tick") return "success";
  return phase;
}
