export function spawnBudgetPerSecond(t: number) {
  // t: 秒，分段曲线：前期快、中期稳、后期爆
  if (t < 60) return 6 + t * 0.06; // 6 -> 9.6
  if (t < 240) return 10 + (t - 60) * 0.03; // 10 -> 15.4
  return 16 + Math.pow((t - 240) / 60, 1.2) * 6; // 16 -> ...
}

export function clampVisualCount(count: number, max: number) {
  // 超过上限时，做降采样/合并表现
  if (count <= max) return { draw: count, merge: 0 };
  return { draw: max, merge: count - max };
}
