type NodeId = string;

type TalentNode = {
  id: NodeId;
  cost: number;
  prereq?: NodeId[];
  exclusive?: NodeId[];
};

export function canUnlock(node: TalentNode, owned: Set<NodeId>, points: number) {
  if (points < node.cost) return false;
  if (node.prereq && node.prereq.some((id) => !owned.has(id))) return false;
  if (node.exclusive && node.exclusive.some((id) => owned.has(id))) return false;
  return true;
}
