type EnemyId = string;
type Enemy = { id: EnemyId; cost: number };

export function pickWave(enemies: Enemy[], budget: number) {
  const picked: Enemy[] = [];
  let remaining = budget;
  for (const enemy of enemies) {
    while (remaining >= enemy.cost) {
      picked.push(enemy);
      remaining -= enemy.cost;
    }
  }
  return picked;
}
