type Wave = { id: number; budget: number; elite: boolean; pattern: string };

export function waveBudget(wave: number) {
  // 预算增长：线性 + 小指数，让后期有加速
  return Math.floor(40 + wave * 12 + Math.pow(wave, 1.25) * 6);
}

export function makeWave(wave: number): Wave {
  const elite = wave % 5 === 0;
  const budget = waveBudget(wave) * (elite ? 1.35 : 1);
  const pattern = elite ? "elite_mix" : wave % 3 === 0 ? "fast" : "swarm";
  return { id: wave, budget, elite, pattern };
}
